    ?c skos:notation ?urn. 
  }
  OPTIONAL {
    ?c skos:prefLabel ?l.
    FILTER( LANG(?l) = 'en') 
  }
  OPTIONAL {
    ?c skos:prefLabel ?a.
    FILTER( LANG(?a) = 'it') 
  }
  FILTER( REGEX( STR(?l), '$search_param', 'i') || REGEX( STR(?a), '$search_param', 'i') ) 
}
ORDER BY ASC(?l) ASC(?a)
