sparql.query:

PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
SELECT ?c ?l
WHERE {
  ?c rdf:type skos:Concept .
  OPTIONAL {
    ?c skos:prefLabel ?l .
    FILTER( LANG(?l) = "en")
  }
  FILTER( REGEX( STR(?l), "$search_param", "i") )
}
ORDER BY ASC(?l) ASC(?a)

singleton.query:

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX dct: <http://purl.org/dc/terms/>
SELECT ?l ?date
WHERE {
  ?voc skos:member <$search_param> .
  OPTIONAL {
    ?voc skos:prefLabel ?l .
  }
  OPTIONAL {
    ?voc dct:date ?ts .
  }
  BIND( STRBEFORE(?ts , " ") AS ?date )
}

sparql.query:

PREFIX skos: <http://www.w3.org/2004/02/skos/core#> 
SELECT ?c ?l ?a
FROM <http://ritmare.it/rdfdata/parameters>
WHERE { 
  ?c rdf:type skos:Concept.
  OPTIONAL {
  ?c skos:prefLabel ?l.
  FILTER( LANG(?l) = "en")
  }
  OPTIONAL {
  ?c skos:prefLabel ?a.
  FILTER( LANG(?a) = "it")
  }
  FILTER( REGEX( STR(?l), "$search_param", "i") || REGEX( STR(?a), "$search_param", "i") )
  }
ORDER BY ASC(?l) ASC(?a)
