item.hasValue:
PREFIX ns: <http://www.w3.org/2006/vcard/ns#> 
SELECT ?l
FROM <http://ritmare.it/rdfdata/project>
WHERE { 
  OPTIONAL {
    <$1$> foaf:phone ?l.
  }
}

PREFIX addr: <http://wymiwyg.org/ontologies/foaf/postaddress#> 
SELECT ?l
FROM <http://ritmare.it/rdfdata/project>
WHERE { 
  OPTIONAL {
    <$1$> addr:thoroughfareName ?l.
  }
}

